import React, { useState, useEffect } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const [error, setError] = useState(null);

  useEffect(() => {
    fetchMoviesHandler();
  }, []);

  async function fetchMoviesHandler() {
    setIsLoading(true);
    setError(null);
    // using then method

    // fetch("https://swapi.dev/api/films/")
    //   .then((response) => response.json())
    //   .then((data) => {
    //     const tranformedData = data.results.map((movieData) => {
    //       return {
    //         id: movieData.episode_id,
    //         title: movieData.title,
    //         openingText: movieData.opening_crawl,
    //         releaseDate: movieData.release_date,
    //       };
    //     });
    //     setMovies(tranformedData);
    //   });

    // using async and await

    try {
      const response = await fetch("https://swapi.dev/api/film/");

      // if an error occurs then throw an error
      if (!response.ok) {
        throw new Error("Something went wrong");
      }
      const data = await response.json();

      const transformedData = data.results.map((movieData) => {
        return {
          id: movieData.episode_id,
          title: movieData.title,
          openingText: movieData.opening_crawl,
          releaseDate: movieData.release_date,
        };
      });

      setMovies(transformedData);
    } catch (error) {
      setError(error.message);
    }

    setIsLoading(false);
  }

  let content = <p>No Movie Found</p>;

  if (error) content = <p>{error}</p>;
  else if (isLoading) content = <p>Loading ...</p>;
  else if (movies.length > 0) content = <MoviesList movies={movies} />;

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>{content}</section>
    </React.Fragment>
  );
}

export default App;
