import React, { useState, useEffect, useCallback } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";
import AddMovie from "./components/AddMovie";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const [error, setError] = useState(null);

  const fetchMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    // using then method

    // fetch("https://swapi.dev/api/films/")
    //   .then((response) => response.json())
    //   .then((data) => {
    //     const tranformedData = data.results.map((movieData) => {
    //       return {
    //         id: movieData.episode_id,
    //         title: movieData.title,
    //         openingText: movieData.opening_crawl,
    //         releaseDate: movieData.release_date,
    //       };
    //     });
    //     setMovies(tranformedData);
    //   });

    // using async and await

    try {
      // without .json firebase willn not accept the request, /movies will create a node named movies in the database
      const response = await fetch(
        "https://react-http-25049-default-rtdb.firebaseio.com/movies.json"
      );

      // if an error occurs then throw an error
      if (!response.ok) {
        throw new Error("Something went wrong");
      }
      const data = await response.json();

      console.log(data);

      const transformedData = data.results.map((movieData) => {
        return {
          id: movieData.episode_id,
          title: movieData.title,
          openingText: movieData.opening_crawl,
          releaseDate: movieData.release_date,
        };
      });

      setMovies(transformedData);
    } catch (error) {
      setError(error.message);
    }

    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchMoviesHandler();
  }, [fetchMoviesHandler]);

  async function addMovieHandler(movie) {
    const responde = await fetch(
      "https://react-http-25049-default-rtdb.firebaseio.com/movies.json",
      {
        method: "POST",
        body: JSON.stringify(movie),
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
  }

  let content = <p>No Movie Found</p>;

  if (error) content = <p>{error}</p>;
  else if (isLoading) content = <p>Loading ...</p>;
  else if (movies.length > 0) content = <MoviesList movies={movies} />;

  return (
    <React.Fragment>
      <section>
        <AddMovie onAddMovie={addMovieHandler} />
      </section>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>{content}</section>
    </React.Fragment>
  );
}

export default App;
