import { useState } from "react";
import "./FormInput.css";
import Modal from "../Modal/Modal";

export default function FormInput(props) {
  const [isWrongInput, setWrongInput] = useState(false);
  const buttonHandler = (event) => {
    event.preventDefault();

    setWrongInput(true);
  };

  const modalHandler = (state) => {
    setWrongInput(false);
  };

  return (
    <div>
      <div className={`${isWrongInput ? "hidden" : ""}`}></div>
      <form action="">
        <div>
          <label htmlFor="name">Name</label>
          <input type="text" id="name" placeholder="Name" />
        </div>
        <div>
          <label htmlFor="marks">Agw (Years)</label>
          <input type="number" id="marks" placeholder="Marks" />
        </div>

        <button type="submit" onClick={buttonHandler}>
          Submit
        </button>
      </form>

      {isWrongInput && (
        <Modal content={"Wrong submission"} onCloseModal={modalHandler} />
      )}
    </div>
  );
}
