import { useState, useRef } from "react";
import "./FormInput.css";
import Modal from "../Modal/Modal";
import { v4 as uuid } from "uuid";

export default function FormInput(props) {
  const inputNameRef = useRef();
  const inputAgeRef = useRef();
  const [isWrongInput, setWrongInput] = useState(false);
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const inputHandler = (type, value) => {
    if (type === "name") setName(value);
    else setAge(value);
  };

  const buttonHandler = (event) => {
    event.preventDefault();

    const name = inputNameRef.current.value;
    const age = inputAgeRef.current.value;

    if (name.trim().length === 0 || age.trim().length === 0) {
      if (name.trim().length === age.trim().length) {
        setErrorMessage("Enter both the name and the age ( in years )..");
      } else if (name.trim().length === 0) {
        setErrorMessage("Enter the name...");
      } else {
        setErrorMessage("Enter the age");
      }

      setWrongInput(true);
      return;
    } else if (+age < 0) {
      setErrorMessage("Entered Age must be > 0");
      setWrongInput(true);
      return;
    }

    props.onSubmitForm({ name, age, id: uuid() });
    setName("");
    setAge("");
  };

  const modalHandler = (state) => {
    setWrongInput(false);
  };

  return (
    <div>
      <div className={`${isWrongInput ? "hidden" : ""}`}></div>
      <form action="">
        <div>
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            placeholder="Name"
            onChange={(event) => inputHandler("name", event.target.value)}
            value={name}
            autoComplete="off"
            ref={inputNameRef}
          />
        </div>
        <div>
          <label htmlFor="age">Age (Years)</label>
          <input
            type="number"
            id="age"
            placeholder="Age"
            onChange={(event) => inputHandler("age", event.target.value)}
            value={age}
            autoComplete="off"
            ref={inputAgeRef}
          />
        </div>

        <button type="submit" onClick={buttonHandler}>
          Submit
        </button>
      </form>

      {isWrongInput && (
        <Modal content={errorMessage} onCloseModal={modalHandler} />
      )}
    </div>
  );
}
