import React from "react";

import "./ExpensesFilter.css";

const ExpensesFilter = (props) => {
  const selectHandler = (event) => {
    console.log("INside select handler");
    console.log(props);
    props.onSaveYear(event.target.value);
  };
  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select>
          <option value="2022" onSelect={selectHandler}>
            2022
          </option>
          <option value="2021" onSelect={selectHandler}>
            2021
          </option>
          <option value="2020" onSelect={selectHandler}>
            2020
          </option>
          <option value="2019" onSelect={selectHandler}>
            2019
          </option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
