import { useState } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredEmail, setEnteredEmail] = useState("");
  const [fieldTouched, setFieldTouched] = useState(false);
  const [enteredEmailIsValid, setEmailIsValid] = useState(false);

  const enteredNameIsValid = enteredName.trim() !== "";
  const inputIsInvalid = !enteredNameIsValid && fieldTouched;
  const formIsValid = enteredNameIsValid;
  const emailInputIsInvalid = !enteredEmailIsValid && fieldTouched;

  if (enteredName.trim() !== "" && !fieldTouched) setFieldTouched(true);

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const emailChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
  };

  const nameInputBlurHandler = (event) => {
    setFieldTouched(true);
  };

  const emailBlurHandler = (event) => {
    setFieldTouched(true);
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();
    setFieldTouched(true);

    if (enteredName.trim() === "") return;

    if (!enteredEmail.trim().includes("@")) {
      setEmailIsValid(false);
    }

    setEnteredName("");
    setEnteredNameTouched(false);
  };

  const nameInputClasses = `form-control ${inputIsInvalid ? "invalid" : ""}`;
  const emailInputClasses = `form-control ${
    !enteredEmailIsValid ? "invalid" : ""
  }`;

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameInputChangeHandler}
          value={enteredName}
          onBlur={nameInputBlurHandler}
        />
      </div>

      <div className={emailInputClasses}>
        <label htmlFor="email">Your email</label>
        <input
          type="email"
          id="email"
          onChange={emailChangeHandler}
          value={enteredEmail}
        />
      </div>

      {inputIsInvalid && <p className="error-text">Name must not be empty</p>}
      <div className="form-actions">
        <button
          disabled={!formIsValid}
          onClick={() => {
            console.log("clicked");
          }}
        >
          Submit
        </button>
      </div>
    </form>
  );
};

export default SimpleInput;
