import { useState } from "react";

import useInput from "../hooks/use-input";

const SimpleInput = (props) => {
  const {
    value: enteredName,
    hasError: nameInputHasError,
    valueChangeHandler: nameChangeHandler,
    valueBlurHandler: nameBlurHandler,
  } = useInput((name) => name.trim() !== "");
  // const [enteredName, setEnteredName] = useState("");
  // const [nameFieldTouched, setNameFieldTouched] = useState(false);

  const [enteredEmail, setEnteredEmail] = useState("");
  const [emailFieldTouched, setEmailFieldTouched] = useState(false);

  const enteredNameIsValid = enteredName.trim() !== "";
  const inputIsInvalid = !enteredNameIsValid && nameFieldTouched;
  const enteredEmailIsValid = enteredEmail.includes("@");
  const inputEmailIsInvalid = !enteredEmailIsValid && emailFieldTouched;
  const formIsValid = enteredNameIsValid && enteredEmailIsValid;

  if (nameFieldTouched === false && enteredName.trim() !== "")
    setNameFieldTouched(true);

  if (emailFieldTouched === false && enteredEmail.trim() !== "")
    setEmailFieldTouched(true);

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const nameInputBlurHandler = (event) => {
    setNameFieldTouched(true);
  };

  const emailInputChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
  };

  const emailBlurhander = (event) => {
    setEmailFieldTouched(true);
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();
    setNameFieldTouched(true);
    setEmailFieldTouched(true);

    if (enteredName.trim() === "" || !enteredEmail.includes("@")) return;

    setEnteredName("");
    setEnteredEmail("");
    setNameFieldTouched(false);
    setEmailFieldTouched(false);
  };

  const nameInputClasses = `form-control ${inputIsInvalid ? "invalid" : ""}`;
  const emailInputClasses = `form-control ${
    inputEmailIsInvalid ? "invalid" : ""
  }`;

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameInputChangeHandler}
          value={enteredName}
          onBlur={nameInputBlurHandler}
        />
      </div>

      {inputIsInvalid && <p className="error-text">Name must not be empty</p>}

      <div className={emailInputClasses}>
        <label htmlFor="email">Your Email</label>
        <input
          type="email"
          id="email"
          onChange={emailInputChangeHandler}
          value={enteredEmail}
          onBlur={emailBlurhander}
        />
      </div>

      {inputEmailIsInvalid && (
        <p className="error-text">Email must include '@'</p>
      )}

      <div className="form-actions">
        <button
          disabled={!formIsValid}
          onClick={() => {
            console.log("clicked");
          }}
        >
          Submit
        </button>
      </div>
    </form>
  );
};

export default SimpleInput;
